import React, { useState, ChangeEvent, useRef, useMemo, useEffect } from 'react';
import './App.css';

function App() {
  const [image, setImage] = useState<string | null>(null);
  const [images, setImages] = useState<{ src: string; editedSrc?: string | null }[]>([]);
  const [editedImage, setEditedImage] = useState<string | null>(null);
  const [originalImage, setOriginalImage] = useState<string | null>(null);
  const [imageSize, setImageSize] = useState<{ width: number; height: number } | null>(null);
  const [uploadedImageResolution, setUploadedImageResolution] = useState<{ width: number; height: number }>({ width: 0, height: 0 });
  const uploadedImageRef = useRef<HTMLImageElement>(null);
  const [blurStep, setBlurStep] = useState<number>(0);
  const [prevBlurSteps, setPrevBlurSteps] = useState<string[]>([]);
  const [isBlackAndWhite, setIsBlackAndWhite] = useState<boolean>(false);
  const [compressedImage, setCompressedImage] = useState<string | null>(null);
  const [prevBitmapImage, setPrevBitmapImage] = useState<string | null>(null);
  const [customResolution, setCustomResolution] = useState<{ width: number; height: number }>({ width: 0, height: 0 });

  useEffect(() => {
    const updateImageSize = () => {
      if (uploadedImageRef.current) {
        const { width, height } = uploadedImageRef.current;
        setImageSize({ width, height });
      }
    };

    updateImageSize();
    window.addEventListener('resize', updateImageSize);

    return () => {
      window.removeEventListener('resize', updateImageSize);
    };
  }, [editedImage, image]);

  useEffect(() => {
    if (uploadedImageRef.current && image) {
      const img = new Image();
      img.onload = () => {
        setUploadedImageResolution({ width: img.width, height: img.height });
      };
      img.src = image;
    }
  }, [image]);

  const handleImageChange = (e: ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files && e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      if (typeof reader.result === 'string') {
        setImage(reader.result);
        setOriginalImage(reader.result);
      }
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const convertToBlackAndWhite = () => {
    if (uploadedImageRef.current) {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!!;
      canvas.width = uploadedImageRef.current.naturalWidth;
      canvas.height = uploadedImageRef.current.naturalHeight;
      ctx.drawImage(uploadedImageRef.current, 0, 0);
      const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
      const data = imageData.data;
      for (let i = 0; i < data.length; i += 4) {
        const avg = (data[i] + data[i + 1] + data[i + 2]) / 3;
        data[i] = avg;
        data[i + 1] = avg;
        data[i + 2] = avg;
      }
      ctx.putImageData(imageData, 0, 0);
      const editedSrc = canvas.toDataURL();
      setEditedImage(editedSrc);
      uploadedImageRef.current.src = editedSrc;
    }
  };

  const rotateImage = () => {
    if (uploadedImageRef.current) {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!!;
      canvas.width = uploadedImageRef.current.naturalHeight;
      canvas.height = uploadedImageRef.current.naturalWidth;
      ctx.translate(canvas.width / 2, canvas.height / 2);
      ctx.rotate(Math.PI / 2);
      ctx.drawImage(uploadedImageRef.current, -uploadedImageRef.current.naturalWidth / 2, -uploadedImageRef.current.naturalHeight / 2);
      const editedSrc = canvas.toDataURL();
      setEditedImage(editedSrc);
      uploadedImageRef.current.src = editedSrc;
    }
  };

  const compressImage = () => {
    if (uploadedImageRef.current) {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!!;
      canvas.width = uploadedImageRef.current.naturalWidth / 2;
      canvas.height = uploadedImageRef.current.naturalHeight / 2;
      ctx.drawImage(uploadedImageRef.current, 0, 0, canvas.width, canvas.height);
      const editedSrc = canvas.toDataURL();
      setCompressedImage(editedSrc);
      setEditedImage(editedSrc);
      uploadedImageRef.current.src = editedSrc;
    }
  };

  const decompressImage = () => {
    if (uploadedImageRef.current && compressedImage) {
      uploadedImageRef.current.src = originalImage!;
      setEditedImage(originalImage);
      setCompressedImage(null);
    }
  };

  const convertToBitmap = () => {
    if (uploadedImageRef.current) {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!!;
      canvas.width = uploadedImageRef.current.naturalWidth;
      canvas.height = uploadedImageRef.current.naturalHeight;
      ctx.drawImage(uploadedImageRef.current, 0, 0);
      const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
      const data = imageData.data;
      for (let i = 0; i < data.length; i += 4) {
        const avg = (data[i] + data[i + 1] + data[i + 2]) / 3;
        data[i] = avg < 128 ? 0 : 255;
        data[i + 1] = avg < 128 ? 0 : 255;
        data[i + 2] = avg < 128 ? 0 : 255;
      }
      ctx.putImageData(imageData, 0, 0);
      const editedSrc = canvas.toDataURL();
      setEditedImage(editedSrc);
      uploadedImageRef.current.src = editedSrc;
    }
  };

  const handleStoreImage = () => {
    if (uploadedImageRef.current && image) {
      setImages(prevImages => [...prevImages, { src: image, editedSrc: editedImage }]);
      setImage(null);
      setEditedImage(null);
    }
  };

  const mirrorImage = () => {
    if (uploadedImageRef.current) {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!!;
      canvas.width = uploadedImageRef.current.naturalWidth;
      canvas.height = uploadedImageRef.current.naturalHeight;
      ctx.translate(canvas.width, 0);
      ctx.scale(-1, 1);
      ctx.drawImage(uploadedImageRef.current, 0, 0);
      const editedSrc = canvas.toDataURL();
      setEditedImage(editedSrc);
      uploadedImageRef.current.src = editedSrc;
    }
  };

  const blurOrSharpenImage = (amount: number) => {
    if (uploadedImageRef.current) {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!!;
      canvas.width = uploadedImageRef.current.naturalWidth;
      canvas.height = uploadedImageRef.current.naturalHeight;
      ctx.filter = `blur(${amount}px)`;
      ctx.drawImage(uploadedImageRef.current, 0, 0);
      const editedSrc = canvas.toDataURL();
      setPrevBlurSteps(prevSteps => [...prevSteps, editedSrc]);
      setEditedImage(editedSrc);
      uploadedImageRef.current.src = editedSrc;
    }
  };

  const handleBlurImage = () => {
    if (uploadedImageRef.current) {
      blurOrSharpenImage(3);
      setBlurStep(prevStep => prevStep + 1);
    }
  };

  const handleUnblurImage = () => {
    if (blurStep > 0) {
      setBlurStep(prevStep => prevStep - 1);
      const prevImage = prevBlurSteps.pop();
      if (prevImage) {
        setEditedImage(prevImage);
        uploadedImageRef.current!.src = prevImage;
      }
    } else {
      setEditedImage(originalImage);
      if (originalImage) {
        uploadedImageRef.current!.src = originalImage;
      }
    }
  };

  const handleCustomResolutionChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setCustomResolution(prevResolution => ({
      ...prevResolution,
      [name]: parseInt(value) || 0 // Garante que o valor seja um número inteiro
    }));
  };

  const handleApplyCustomResolution = () => {
    if (uploadedImageRef.current) {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!!;
      canvas.width = customResolution.width;
      canvas.height = customResolution.height;
      ctx.drawImage(uploadedImageRef.current, 0, 0, canvas.width, canvas.height);
      const editedSrc = canvas.toDataURL();
      setEditedImage(editedSrc);
      uploadedImageRef.current.src = editedSrc;
    }
  };

  const handleStoreCustomResolution = () => {
    if (uploadedImageRef.current && editedImage) {
      setImages(prevImages => [...prevImages, { src: image!, editedSrc: editedImage }]);
    }
  };

  const removeLastImage = () => {
    setImages(prevImages => prevImages.slice(0, -1));
  };

  return (
    <div className="App">
      <div>
        <label htmlFor="image-upload">Upload Image:</label>
        <input type="file" id="image-upload" accept="image/*" onChange={handleImageChange} />
      </div>
      {image && (
        <div className="image-container">
          <img ref={uploadedImageRef} src={image} alt="Uploaded" />
          <div className="buttons-container">
            <button onClick={handleStoreImage}>Store Image</button>
            <button onClick={convertToBlackAndWhite}>Black & White</button>
            <button onClick={rotateImage}>Rotate</button>
            <button onClick={compressImage}>Compress</button>
            <button onClick={decompressImage}>Decompress</button>
            <button onClick={convertToBitmap}>Bitmap</button>
            <button onClick={mirrorImage}>Mirror</button>
            <button onClick={handleBlurImage}>Blur</button>
            <button onClick={handleUnblurImage}>Unblur</button>
            <button onClick={removeLastImage}>Remove Last Image</button>
            <div className="custom-resolution-container">
              <label>
                Width:
                <input
                  type="number"
                  name="width"
                  value={customResolution.width}
                  onChange={handleCustomResolutionChange}
                />
              </label>
              <label>
                Height:
                <input
                  type="number"
                  name="height"
                  value={customResolution.height}
                  onChange={handleCustomResolutionChange}
                />
              </label>
              <button onClick={handleApplyCustomResolution}>Apply Custom Resolution</button>
            </div>
          </div>
        </div>
      )}
      <div className="image-list">
        {images.map((img, index) => (
          <div key={index} className="image-item">
            <img src={img.src} alt={`Stored ${index}`} />
            {img.editedSrc && <img src={img.editedSrc} alt={`Edited ${index}`} />}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
