import React, { useState, ChangeEvent, useRef, useMemo } from 'react';
import './App.css';

function App() {
  const [image, setImage] = useState<string | null>(null);
  const [images, setImages] = useState<{ src: string; editedSrc?: string | null }[]>([]);
  const [editedImage, setEditedImage] = useState<string | null>(null);
  const [originalImage, setOriginalImage] = useState<string | null>(null); // State para armazenar a imagem original
  const uploadedImageRef = useRef<HTMLImageElement>(null);
  const [blurSteps, setBlurSteps] = useState<string[]>([]); // Lista para armazenar os estados de imagem após o borramento

  const handleImageChange = (e: ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files && e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      if (typeof reader.result === 'string') {
        setImage(reader.result);
        setOriginalImage(reader.result); // Define a imagem original quando é carregada
      }
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const applyBlur = (amount: number) => {
    if (uploadedImageRef.current) {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!!;
      canvas.width = uploadedImageRef.current.width;
      canvas.height = uploadedImageRef.current.height;
      ctx.filter = `blur(${amount}px)`;
      ctx.drawImage(uploadedImageRef.current, 0, 0);
      const editedSrc = canvas.toDataURL();
      setBlurSteps(prevSteps => [...prevSteps, editedSrc]); // Adiciona o estado da imagem após o borramento à lista
      setEditedImage(editedSrc);
      uploadedImageRef.current.src = editedSrc;
    }
  };

  const handleBlurImage = () => {
    if (uploadedImageRef.current) {
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!!;
      canvas.width = uploadedImageRef.current.width;
      canvas.height = uploadedImageRef.current.height;
      ctx.drawImage(uploadedImageRef.current, 0, 0);
      const editedSrc = canvas.toDataURL();
      setOriginalImage(editedSrc); // Salva a imagem atual como original antes de aplicar o borramento
      applyBlur(3); // Altere o valor de acordo com a quantidade de desfoque desejada
    }
  };

  const handleUnblurImage = () => {
    if (blurSteps.length > 0) {
      // Remove o último estado da lista
      const prevImageSrc = blurSteps.pop(); 
      if (prevImageSrc) {
        setEditedImage(prevImageSrc); // Restaura o estado anterior da imagem
      }
    } else if (editedImage && originalImage) {
      setEditedImage(originalImage); // Restaura a imagem original
    }
  };

  const imageList = useMemo(
    () => images.map((img, index) => (
      <img
        key={index}
        src={img.editedSrc || img.src}
        alt={`Uploaded Image ${index}`}
        style={{ width: '100px', height: 'auto', margin: '5px', cursor: 'pointer' }}
        onClick={() => {
          setImage(img.src);
          setEditedImage(img.editedSrc || null);
        }}
      />
    )),
    [images]
  );

  return (
    <div style={{
      fontFamily: 'Arial, sans-serif',
      margin: 0,
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      background: 'linear-gradient(to right, #4e54c8, #8f94fb)',
      width: '100vw',
      overflow: 'hidden'
    }}>
      <div style={{
        backgroundColor: 'rgba(255, 255, 255, 0.9)',
        padding: '20px',
        borderRadius: '10px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        textAlign: 'center',
        width: '80%'
      }}>
        <input type="file" id="imageInput" accept="image/*" onChange={handleImageChange} />
        <div style={{ marginBottom: '20px' }} className="image-container">
          {image && <img ref={uploadedImageRef} src={editedImage || image} alt="Uploaded Image" id="uploadedImage" />}
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }} className="controls">
          {/* Restante dos botões */}
          <button className="button" onClick={handleBlurImage}>Borrar</button>
          <button className="button" onClick={handleUnblurImage}>Desborrar</button>
        </div>
        <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
          {imageList}
        </div>
      </div>
    </div>
  );
}

export default App;
